<?php if ( ! defined('BASEPATH')) exit('No direct script access allowed');class Penjualan_project extends CI_Controller {		function __construct()	{		parent::__construct();		if (!$this->ion_auth->logged_in())		{			//redirect them to the login page			echo '<h4 style="margin-top:10px; display:block; text-align:left"><i class="fa fa-warning txt-color-orangeDark"></i> Your login is expired click <a href="'.base_url().'">here</a> to relogin.</h4>';			die;		}				$this->load->library('Datatables');		$this->load->model('penjualan_model');	}		public function index()	{		//check session alert		$alert = $this->session->userdata('alert_msg');		if ($alert != "") {			$this->session->unset_userdata('alert_msg');		}		$alert != "" ? $disp = '' : $disp = 'display: none;';		$data['alert_block'] = '			<div class="alert alert-block alert-success" style="'.$disp.'" id="alert_table">				<a href="#" data-dismiss="alert" class="close">x</a>				<h4 class="alert-heading"><i class="fa fa-check-square-o"></i> Alert!</h4>				<p class="alert_msg">'.$alert.'</p>			</div>';		// Load the template from the views directory        $this->parser->parse("penjualan.tpl", $data);	}		public function table_list(){		$this->datatables->select('ref_code,								   po_customer,								   customers.nama_customer as customer,								   jumlah,								   tanggal_penjualan,								   status,								   users.username as created_by,								   idtransaksi_penjualan')            ->from('transaksi_penjualan')			->where('type','project')			//->order_by('created_date desc')			//->edit_column('name', '$1', 'strtolower(name)') // php functions			->edit_column('idtransaksi_penjualan', '$1', 'penjualan_project', 'callback_actions(idtransaksi_penjualan)');		$this->datatables->join('customers', 'transaksi_penjualan.idcustomer = customers.idcustomer', 'left');		$this->datatables->join('users', 'transaksi_penjualan.created_by = users.id', 'left');        echo $this->datatables->generate();	}		public function callback_actions($id){		return '<nav class="col-xs-6 col-sm-6 col-md-6 col-lg-6">					<a class="btn btn-success btn-sm" href="'.base_url().'penjualan_project/add/'.$id.'">						<i class="fa fa-edit"></i> <span class="hidden-mobile">Edit Row</span>					</a>									</nav>					<a class="btn btn-success btn-sm" href="#'.base_url().'penjualan_project/view/'.$id.'">						<i class="fa fa-view"></i> <span class="hidden-mobile">View Detail</span>					</a>				';	}		function get_name($table,$field,$find,$value,$add_where=""){	   if(!empty($value)){		  // echo "select ".$field." as result from ".$table." where ".$find."='".$value."' ".$add_where." <br />";		   $q = $this->db->query("select ".$field." as result from ".$table." where ".$find."='".$value."' ".$add_where);		   $rows = $q->result_array();		   		   if(@$rows[0]){				return @$rows[0]['result'];		   }		   else{			   write_log( "parse error/empty result for : select ".$field." as result from ".$table." where ".$find."='".$value."' ".$add_where);		   }	   }	   else{		   return 0;	   }	}		public function add(){		$data[''] = "";		$id = $this->uri->segment(3);		if($id){			$this->db->select("*")->from("transaksi_penjualan")->where("idtransaksi_penjualan",$id);			$q = $this->db->get();			$d = $q->result_array();						//detail			$this->db->select("*")->from("transaksi_penjualan_detail")->where("ref_code",$d[0]['ref_code']);			$qd = $this->db->get();			//echo $this->db->last_query();			$dd = $qd->result_array();						$html_detail = "";			$i = 0;						foreach($dd as $k=>$v){				if(empty($dd[$k]['stock'])){					$location = "Jakarta";				}				else{					$location = "Surabaya";				}				if(!empty($dd[$k]['idref_tinggi'])){ 					 $add = "<td><input type='hidden' name='type[]' value='1' />Height</td>							 <td><input type='hidden' name='idref_specs[]' value='".$dd[$k]['idref_tinggi']."' />".$this->get_name("ref_tinggi","concat(nilai,' ',satuan)","idref",$dd[$k]['idref_tinggi'])."</td>";				}				if(!empty($dd[$k]['idref_lebar'])){ 					 $add = "<td><input type='hidden' name='type[]' value='2' />Lebar</td>							 <td><input type='hidden' name='idref_specs[]' value='".$dd[$k]['idref_lebar']."' />".$this->get_name("ref_lebar","concat(nilai,' ',satuan)","idref",$dd[$k]['idref_lebar'])."</td>";				}				if(!empty($dd[$k]['idref_depth'])){ 					 $add = "<td><input type='hidden' name='type[]' value='3' />Depth</td>							 <td><input type='hidden' name='idref_specs[]' value='".$dd[$k]['idref_depth']."' />".$this->get_name("ref_depth","concat(nilai,' ',satuan)","idref",$dd[$k]['idref_depth'])."</td>";				}				if(!empty($dd[$k]['idref_ambalan'])){ 					 $add = "<td><input type='hidden' name='type[]' value='4' />Ambalan</td>							 <td><input type='hidden' name='idref_specs[]' value='".$dd[$k]['idref_ambalan']."' />".$this->get_name("ref_ambalan","nilai","idref",$dd[$k]['idref_ambalan'])."</td>";				}				if(!empty($dd[$k]['idref_footplate'])){ 					 $add = "<td><input type='hidden' name='type[]' value='5' />Footplate</td>							 <td><input type='hidden' name='idref_specs[]' value='".$dd[$k]['idref_footplate']."' />".$this->get_name("ref_footplate","nilai","idref",$dd[$k]['idref_footplate'])."</td>";				}				if(!empty($dd[$k]['idref_accessories'])){ 					 $add = "<td><input type='hidden' name='type[]' value='6' />Accessories</td>							 <td><input type='hidden' name='idref_specs[]' value='".$dd[$k]['idref_accessories']."' />".$this->get_name("ref_accessories","nilai","idref",$dd[$k]['idref_accessories'])."</td>";				}				$html_detail.="<tr id='list_".$i."'>									  ".$add."								  <td><input type='hidden' name='jumlah[]' value='".$dd[$k]['jumlah']."' />".$dd[$k]['jumlah']."</td>								  <td><input type='hidden' name='stock[]' value='".$dd[$k]['stock']."' />".$location."</td>								  <td><a href='javascript:void(0);' onclick='removeList(".$i.");' class='btn btn-danger'> &nbsp;&nbsp;<i class='fa fa-trash-o'></i> Remove &nbsp;&nbsp;</a></td>							   </tr>";				$i++;			}			$data['cond'] = "Edit";			$data['stat_cond'] = "update";			$data['detail']['list_option_customer'] = $this->getListCustomer($d[0]['idcustomer']);			$data['detail']['po_customer'] = $d[0]['po_customer'];			$data['detail']['idtransaksi_penjualan'] = $d[0]['idtransaksi_penjualan'];			$data['detail']['tanggal_penjualan'] = date("d-m-Y",strtotime($d[0]['tanggal_penjualan']));			$data['detail']['list_option_ref_level'] = $this->getRefList('ref_level');			$data['detail']['list_option_ref_tinggi'] = $this->getRefList('ref_tinggi');			$data['detail']['list_option_ref_lebar'] = $this->getRefList('ref_lebar');			$data['detail']['list_option_ref_depth'] = $this->getRefList('ref_depth');			$data['detail']['list_option_ref_ambalan'] = $this->getRefList('ref_ambalan',0,'N');			$data['detail']['list_option_ref_footplate'] = $this->getRefList('ref_footplate');			$data['detail']['list_option_ref_accessories'] = $this->getRefList('ref_accessories',0,'N');			$data['detail']['index'] = $i;			$data['detail']['list_edit'] = $html_detail;		}		else{			$data['cond'] = "Add";			$data['stat_cond'] = "insert";			$data['detail']['list_option_customer'] = $this->getListCustomer(0);			$data['detail']['po_customer'] = "";			$data['detail']['tanggal_penjualan'] = date("d-m-Y");			$data['detail']['list_specs'] = $this->getRefList('ref_tinggi');/* 			$data['detail']['list_option_ref_level'] = $this->getRefList('ref_level');			$data['detail']['list_option_ref_tinggi'] = $this->getRefList('ref_tinggi');			$data['detail']['list_option_ref_lebar'] = $this->getRefList('ref_lebar');			$data['detail']['list_option_ref_depth'] = $this->getRefList('ref_depth');			$data['detail']['list_option_ref_ambalan'] = $this->getRefList('ref_ambalan',0,'N');			$data['detail']['list_option_ref_footplate'] = $this->getRefList('ref_footplate');			$data['detail']['list_option_ref_accessories'] = $this->getRefList('ref_accessories',0,'N'); */			$data['detail']['list_edit'] = "";			$data['detail']['index'] = 0;		}        $this->parser->parse("add_penjualan.tpl", $data);	}		public function getRefList($table,$id=0,$required="Y"){		$this->db->select("*")->from($table);		$q = $this->db->get();		$d = $q->result_array();		//echo $this->db->last_query();		$html = "";		$selected = "";		$satuan = "";		if($required=="N"){			$html.="<option value=''>- Not Set -</option>";		}				if(empty($id)){			$selected = "selected = 'selected'";		}				foreach($d as $k=>$v){			if($d[$k]["idref"]==$id){				$selected = "selected = 'selected'";			}			if(isset($d[$k]["satuan"])){				$satuan = $d[$k]["satuan"];			}			if(isset($d[$k]["nilai"])){				$text = $d[$k]["nilai"];			}			if(isset($d[$k]["nilai"])){				$text = $d[$k]["nilai"];			}			if(isset($d[$k]["nilai"])){				$text = $d[$k]["nilai"];			}			if($table=="ref_level"){				$html.="<option value='".$d[$k]["nilai"]."' ".$selected.">".$text." level</option>";			}			else{				$html.="<option value='".$d[$k]["idref"]."' ".$selected.">".$text." ".$satuan."</option>";			}			$selected = "";		}				return $html;	}		public function getListCustomer($id){		$this->db->select("*")->from("customers");		$q = $this->db->get();		$d = $q->result_array();		//echo $this->db->last_query();		$html = "";		$selected = "";		foreach($d as $k=>$v){			if($d[$k]["idcustomer"]==$id){				$selected = "selected = 'selected'";			}			$html.="<option value='".$d[$k]["idcustomer"]."' ".$selected.">".$d[$k]["nama_customer"]."</option>";			$selected = "";		}				return $html;	}		public function getLastTrxID($prefix){		$this->db->select("ref_code")->from("transaksi_penjualan")->order_by("idtransaksi_penjualan desc")->limit(1);		$q = $this->db->get();		$d = $q->result_array();		if(!empty($d)){			$last_trx_code = $d[0]['ref_code'];			//PNJ-01022014-0001			$last_number = substr($last_trx_code,12,4);			$next = (int)$last_number+1;		}		else{			$next = 1;		}		$number = $prefix.date("dmY").sprintf("%04d",$next);		return $number;	}		function clear_html($v){		return htmlspecialchars($v,ENT_QUOTES);	}		public function cekstock($id,$jml,$type,$location=""){		if(!empty($id)){			$this->db->select("*")->from("barang");			$this->db->where("idtipe_barang",$type,false);			$this->db->where("idref",$id,false);			$q = $this->db->get();			//echo $this->db->last_query();			$n = $q->num_rows();			if(!empty($n)){				$d = $q->result_array();				if($jml<=$d[0]['quantity'.$location]){					$data['status'] = true;					$data['current_stock'] = $d[0]['quantity'.$location];				}				else{					$data['status'] = false;					$data['current_stock'] = $d[0]['quantity'.$location];				}			}			else{				$data['status'] = false;				$data['current_stock'] = 0;			}			return $data;		}		else{			$data['status'] = true;			return $data;		}	}		public function checkInputStock(){			$jml = $_POST['jumlah'];			$location = $_POST['location'];			//$level = $_POST['idref_level'];			//cek ambalan			$cek_stock = $this->cekstock($_POST['idref_specs'],$jml,$_POST['type'],$location);			if(!$cek_stock['status']){				$data['error'] = true;				$data['field'][0]["name"] = "idref_specs";				$data['field'][0]["message"] = "Stok tidak memenuhi (".$jml."/".$cek_stock['current_stock'].")";				echo json_encode($data);				exit;			}			//cek depth			/* $jml_depth = $level*2*$jml_meja;			$id_depth = $_POST['idref_depth'];			$cek_depth = $this->cekstock($_POST['idref_depth'],$jml_depth,3,$location);			if(!$cek_depth['status']){				$data['error'] = true;				$data['field'][0]["name"] = "idref_depth";				$data['field'][0]["message"] = "Stok depth tidak memenuhi (".$jml_depth."/".$cek_depth['current_stock'].")";				echo json_encode($data);				exit;			}			//cek width			$jml_width = $level*2*$jml_meja;			$id_width = $_POST['idref_lebar'];			$cek_width = $this->cekstock($_POST['idref_lebar'],$jml_width,2,$location);			if(!$cek_width['status']){				$data['error'] = true;				$data['field'][0]["name"] = "idref_lebar";				$data['field'][0]["message"] = "Stok width tidak memenuhi (".$jml_width."/".$cek_width['current_stock'].")";				echo json_encode($data);				exit;			}			//cek height			$jml_height = 4*$jml_meja;			$id_height = $_POST['idref_tinggi'];			$cek_height = $this->cekstock($_POST['idref_tinggi'],$jml_height,1,$location);			if(!$cek_height['status']){				$data['error'] = true;				$data['field'][0]["name"] = "idref_tinggi";				$data['field'][0]["message"] = "Stok height tidak memenuhi (".$jml_height."/".$cek_height['current_stock'].")";				echo json_encode($data);				exit;			}			//cek footplate			$jml_footplate = 4*$jml_meja;			$id_footplate = $_POST['idref_footplate'];			$cek_footplate = $this->cekstock($_POST['idref_footplate'],$jml_footplate,5,$location);			if(!$cek_footplate['status']){				$data['error'] = true;				$data['field'][0]["name"] = "idref_footplate";				$data['field'][0]["message"] = "Stok footplate tidak memenuhi (".$jml_footplate."/".$cek_footplate['current_stock'].")";				echo json_encode($data);				exit;			} */		$data['error'] = false;		echo json_encode($data);	}		public function insert(){		//checking stock		//print_r($_POST);die();					$this->db->trans_start();							//input parent				$total = 0;				foreach($_POST['jumlah'] as $k=>$v){					$total = $total + $_POST['jumlah'][$k];				}				$data_pnj['ref_code'] = $this->getLastTrxID("PNJP"); 				$data_pnj['tanggal_penjualan'] = date("Y-m-d",strtotime($_POST['tanggal_penjualan']));				$data_pnj['idcustomer'] = $_POST['idcustomer'];				$data_pnj['po_customer'] = $_POST['po_customer'];				$data_pnj['jumlah'] = $total;				$data_pnj['status'] = "in progress";				$data_pnj['type'] = "project";				$data_pnj['created_date'] = date("Y-m-d H:i:s");				$data_pnj['created_by'] = $this->session->userdata('user_id');				$this->db->insert("transaksi_penjualan",$data_pnj);								//input detail				foreach($_POST['jumlah'] as $k=>$v){					$type = $_POST['type'][$k];					if($type==1){						$key = "idref_tinggi";					}					if($type==2){						$key = "idref_lebar";					}					if($type==3){						$key = "idref_depth";					}					if($type==4){						$key = "idref_ambalan";					}					if($type==5){						$key = "idref_footplate";					}					if($type==6){						$key = "idref_accessories";					}					$data_detail['jumlah'] = $_POST['jumlah'][$k];					$data_detail['ref_code'] = $data_pnj['ref_code'];					$data_detail[$key] = $_POST['idref_specs'][$k];					$data_detail['stock'] = $_POST['stock'][$k];					$this->db->insert("transaksi_penjualan_detail",$data_detail);					unset($data_detail);				}			$this->db->trans_complete();			$this->session->set_userdata('alert_msg','Data sucessfully inserted');			$data['error'] = false;			echo json_encode($data);	}		public function update(){		//print_r($_POST);		$this->db->trans_start();		$data_update['idcustomer'] = $_POST['idcustomer'];		$data_update['po_customer'] = $_POST['po_customer'];		$data_update['created_date'] = date("Y-m-d H:i:s");		$data_update['created_by'] = $this->session->userdata('user_id');		$data_update['tanggal_penjualan'] = date("Y-m-d",strtotime($_POST['tanggal_penjualan']));		$this->db->where('idtransaksi_penjualan',$_POST['idtransaksi_penjualan']);		$this->db->update("transaksi_penjualan",$data_update);				$ref_code = $this->get_name("transaksi_penjualan","ref_code","idtransaksi_penjualan",$_POST['idtransaksi_penjualan']);		$this->db->where("ref_code",$ref_code);		$this->db->delete("transaksi_penjualan_detail");				if(!empty($_POST['jumlah'])){			foreach($_POST['jumlah'] as $k=>$v){				$type = $_POST['type'][$k];					if($type==1){						$key = "idref_tinggi";					}					if($type==2){						$key = "idref_lebar";					}					if($type==3){						$key = "idref_depth";					}					if($type==4){						$key = "idref_ambalan";					}					if($type==5){						$key = "idref_footplate";					}					if($type==6){						$key = "idref_accessories";					}				$data_detail['jumlah'] = $_POST['jumlah'][$k];				$data_detail['ref_code'] = $ref_code;				$data_detail[$key] = $_POST['idref_specs'][$k];				$data_detail['stock'] = $_POST['stock'][$k];				$this->db->insert("transaksi_penjualan_detail",$data_detail);				unset($data_detail);			}		}				$this->db->trans_complete();				$this->session->set_userdata('alert_msg','Data sucessfully updated');		$data['error'] = false;		echo json_encode($data);	}		public function view(){		$id_view = $this->uri->segment(3);		$this->db->select("a.ref_code,a.jumlah,a.status,a.tanggal_penjualan,b.nama_customer,b.jenis_kelamin,b.alamat,b.telp,b.fax,b.email,b.npwp")->from("transaksi_penjualan a")->where("idtransaksi_penjualan",$id_view,false);		$this->db->join("customers b","a.idcustomer=b.idcustomer","left");		$q = $this->db->get();		//echo $this->db->last_query();		$d = $q->result_array();				//detail		$this->db->select("a.jumlah,b.nilai as level,a.stock,						   concat(c.nilai,' ',c.satuan) as tinggi,						   concat(d.nilai,' ',d.satuan) as lebar,						   concat(e.nilai,' ',e.satuan) as depth,						   f.nilai as nama_ambalan,						   g.nilai as nama_footplate,						   h.nilai as nama_accessories",false)->from("transaksi_penjualan_detail a")->where("ref_code",$d[0]['ref_code']);		$this->db->join("ref_level b","a.idref_level=b.idref","left");		$this->db->join("ref_tinggi c","a.idref_tinggi=c.idref","left");		$this->db->join("ref_lebar d","a.idref_lebar=d.idref","left");		$this->db->join("ref_depth e","a.idref_depth=e.idref","left");		$this->db->join("ref_ambalan f","a.idref_ambalan=f.idref","left");		$this->db->join("ref_footplate g","a.idref_footplate=g.idref","left");		$this->db->join("ref_accessories h","a.idref_accessories=h.idref","left");		$qd = $this->db->get();		//echo $this->db->last_query();		$dd = $qd->result_array();				$detail_text = "";		foreach($dd as $k=>$v){			if(empty($dd[$k]['stock'])){					$location = "Jakarta";				}				else{					$location = "Surabaya";				}			if(!empty($dd[$k]['tinggi'])){				$add = "<td>Height</td><td>".$dd[$k]['tinggi']."</td>";			}			if(!empty($dd[$k]['lebar'])){				$add = "<td>Width</td><td>".$dd[$k]['lebar']."</td>";			}			if(!empty($dd[$k]['depth'])){				$add = "<td>Depth</td><td>".$dd[$k]['depth']."</td>";			}			if(!empty($dd[$k]['nama_ambalan'])){				$add = "<td>Ambalan</td><td>".$dd[$k]['nama_ambalan']."</td>";			}			if(!empty($dd[$k]['nama_footplate'])){				$add = "<td>Footplate</td><td>".$dd[$k]['nama_footplate']."</td>";			}			if(!empty($dd[$k]['nama_accessories'])){				$add = "<td>Accessories</td><td>".$dd[$k]['nama_accessories']."</td>";			}					$detail_text .= "<tr>								".$add."								<td>".$dd[$k]['jumlah']."</td>								<td>".$location."</td>							 </tr>";		}		if($d[0]['jenis_kelamin']=="L"){			$pref = "Mr. ";		}		else{			$pref = "Mrs. ";		}		$button_list = "";		if($d[0]['status']=="on delivery"){			$button_list = '<button class="btn btn-primary" onclick="document.location.replace(\''.base_url().'cpanel.html#penjualan_project/print_sj/'.$d[0]['ref_code'].'\')">												<i class="fa fa-envelope-o  "></i>												Print Surat Jalan											</button>											<button class="btn btn-primary" onclick="document.location.replace(\''.base_url().'cpanel.html#penjualan_project/print_invoice/'.$d[0]['ref_code'].'\')">												<i class="fa fa-money "></i>												Print Invoice											</button>';		}		$data['content'] = '				<div>					<small>Date : <strong>'.date("d/m/Y",strtotime($d[0]['tanggal_penjualan'])).'</strong>,  TRX NO : <strong>'.$d[0]['ref_code'].'</strong></small>					<h2>'.$pref.$d[0]['nama_customer'].'					<br>					<small> '.$d[0]['alamat'].'</small></h2>					<ul class="list-unstyled">						<li>							<p class="text-muted">								<i class="fa fa-phone"></i>&nbsp;&nbsp;/&nbsp;&nbsp;<i class="fa fa-file-text-o"></i> &nbsp;&nbsp; '.$d[0]['telp'].' / '.$d[0]['fax'].' 							</p>						</li>						<li>							<p class="text-muted">								<i class="fa fa-envelope"></i>&nbsp;&nbsp; <a class="btn btn-default btn-xs" href="mailto:'.$d[0]['email'].'"><i class="fa fa-envelope-o"></i> '.$d[0]['email'].'</a>							</p>						</li>						<li>							<p class="text-muted">								<i class="fa fa-envelope"></i>&nbsp;&nbsp; NPWP : &nbsp;<span class="txt-color-darken">'.$d[0]['npwp'].'</span>							</p>						</li>						<li>							<p class="text-muted">								<i class="fa fa-exchange"></i>&nbsp;&nbsp;<span class="txt-color-darken"><strong><a href="javascript:void(0)" onclick="updateStatusTrans(\''.$d[0]['ref_code'].'\',\''.$d[0]['status'].'\')">'.$d[0]['status'].'</a></strong></span>							</p>						</li> 						 					</ul>					<br>					<div class="table-responsive">									<table class="table table-bordered table-striped">										<thead>											<tr>												<th>Type</th>												<th>Specs</th>												<th>Jumlah</th>												<th>Location</th>																							</tr>										</thead>										<tbody>											'.$detail_text.'										</tbody>									</table>														</div>					<form class="smart-form" novalidate="novalidate">						<footer>								<div class="row">									<div class="col-md-12">										<nav>											<a id="cancel" href="penjualan" class="btn btn-default">												Cancel											</a>										</nav>										'.$button_list.'									</div>								</div>							</footer>					</form>				</div>';				$this->parser->parse("view_penjualan.tpl", $data);			}		public function updateStatus(){		if($_POST['status']=='in progress'){			//print_r($_POST);die();			$this->db->trans_start();			//insert to gudang			$this->db->select("*")->from('transaksi_penjualan_detail')->where('ref_code',$_POST['ref']);			$q = $this->db->get();			