 where idbarang = ".$dd[0]['idbarang']);					}					else{						echo "error query : ".$this->db->last_query();die();					}				}			}						$data_trans['status'] = "on delivery";			$this->db->where('ref_code',$_POST['ref']);			$this->db->update('transaksi_penjualan',$data_trans);			if(!$this->db->affected_rows()){				echo "error query : ".$this->db->last_query();die();			}			//echo $this->db->last_query();			$this->db->trans_complete();			echo "ok";		}				if($_POST['status']=='on delivery'){			$this->db->trans_start();			$main_data['status'] = "done";			$this->db->where('ref_code',$_POST['ref']);			$this->db->update('transaksi_penjualan',$main_data);			$this->db->trans_complete();						echo "ok";		}					}		public function print_sj(){		$this->db->select("*")->from("transaksi_penjualan")->where("ref_code",$this->uri->segment(3));		$q = $this->db->get();		$d = $q->result_array();				$this->db->select("*")->from("transaksi_penjualan_detail")->where("ref_code",$this->uri->segment(3));		$qd = $this->db->get();		$dd = $qd->result_array();				$total_unit = "";		/* foreach($dd as $k=>$v){			$total_unit.= $dd[$k]['jumlah']." unit ".$dd[$k]['idref_level']." level <br />";		} */								$this->db->select("*")->from("customers")->where("idcustomer",$d[0]['idcustomer']);		$qc = $this->db->get();		$dc = $qc->result_array();				$data['penjualan'] = $d[0];		$data['penjualan_detail'] = $dd;		$detail_content = '<table class="table table-hover">								<thead>									<tr>										<th>No</th>										<th>DESCRIPTION</th>										<th class="text-center">QTY</th>										<th>UNIT WEIGHT</th>										<th>TOTAL WEIGHT</th>									</tr>								</thead>								<tbody>								';		$grand_total = 0;		$i = 1;		foreach($dd as $kd=>$vd){			/* $total_unit = $dd[$kd]['jumlah']." unit ".$dd[$kd]['idref_level']." level";			$detail_content.='<tr>									<td colspan="5"><strong>'.$total_unit.'</strong></td>								</tr>'; */			$this->db->select("*")->from("gudang")->where("ref_code",$this->uri->segment(3))->where('type','keluar');			$this->db->where("idtransaksi_penjualan_detail",$dd[$kd]["idtransaksi_penjualan_detail"]);			$qgd = $this->db->get();			$dgd = $qgd->result_array();			$no_surat_jalan = $dgd[0]['no_surat_jalan'];			foreach($dgd as $k=>$v){				$this->db->select("*")->from("barang")->where("idbarang",$dgd[$k]["idbarang"]);				$qb = $this->db->get();				$db = $qb->result_array();				$total_berat = $db[0]['berat']*$dgd[$k]['jumlah'];				$detail_content.='<tr>									<td class="text-center"><strong>'.$i.'</strong></td>									<td><a href="javascript:void(0);">'.$db[0]['nama_barang'].'</a></td>									<td class="text-center">'.$dgd[$k]['jumlah'].'</td>									<td>'.$db[0]['berat'].'</td>									<td>'.$total_berat.'</td>								</tr>';				$grand_total+=$total_berat;				$i++;			}		}		$detail_content.='<tr>										<td colspan="4">GRAND TOTAL</td>										<td><strong>'.$grand_total.' Kg</strong></td>									</tr>								</tbody>							</table>';		$data['no_surat_jalan'] = $no_surat_jalan;		$data['detail_content'] = $detail_content;		$data['customer'] = $dc[0];		$this->parser->parse("surat_jalan.tpl", $data);	}		public function print_invoice(){		 /*   $printer="Microsoft XPS Document Writer";		   $content = "Test Donk cooooy\n";		   $content.="=============================================================================== \n           Terima Kasih \n ESC c 3 0";		   $ph = printer_open($printer);		   if($ph)			{			   printer_start_doc($ph, "Invoice");			   printer_start_page($ph);			   $font=printer_create_font("Arial",25,6,PRINTER_FW_THIN,false,false,false,0); 			   printer_set_option($ph, PRINTER_MODE, "raw");			   printer_set_option($ph, PRINTER_PAPER_FORMAT, PRINTER_FORMAT_LETTER);			   printer_select_font($ph,$font);			   printer_write($ph, $content);			   printer_end_page($ph);			   printer_end_doc($ph);			   printer_close($ph)